blockquote {
  border-left: 4px solid var(--accent-blue) !important;
  background-color: var(--bg-secondary) !important;
  padding: 1em;
  margin: 1em 0;
  color: var(--text-secondary) !important;
}

blockquote strong {
  color: var(--accent-blue) !important;
}

