  background-color: var(--bg-tertiary) !important;
  border: 1px solid var(--border-color) !important;
  color: var(--text-primary) !important;
}

#searchbar:focus {
  border-color: var(--accent-blue) !important;
  outline: none;
}

/* Navigation buttons */
